css:
/* ------------------------------------------------------------------------------ */

#page-home-page { }

.page_content {
    padding-top: 40px;
}
.main_wrapper.banner_sec {
  overflow: hidden;
  position: relative;
  border: 1px solid #e4e4e4;
}
.main_wrapper.banner_sec h1 {
  background: #2b955a none repeat scroll 0 0;
  bottom: 0;
  color: #fff;
  font-weight: 600;
  padding: 20px 40px;
  position: absolute;
  right: 0;
  margin: 0;
}
header {
  border-bottom: 5px solid #2b955a;
}

#page-home-page .main-title-wrapper{
	text-align: center;
	clear: both;
	margin-bottom: 25px;
}

#page-home-page .main-title-wrapper span{
	display: inline-block;

}

#page-home-page .main-title{
	padding: 28px 35px 32px 35px;
	background: #ede;
}

#page-home-page .card-wrapper{
	text-align: center;
}
.card-wrapper .bootcards-summary-item {
  background: #ffffff !important;
  box-shadow: 0 0 5px 0 rgba(0, 0, 0, 0.2);
  display: block;
  margin: 40px 0 30px;
  padding: 20px;
  position: relative;
}


.info .icon {
  left: 0;
  position: absolute;
  top: -40px;
  width: 100%;
}

.icon > img {
  background: #2b955a none repeat scroll 0 0;
  border-radius: 50%;
  box-shadow: 0 0 0 3px rgba(0, 0, 0, 0.2);
  padding: 8px;
}

.info h1 {
  color: #222;
  font-weight: 600;
  margin: 35px 0 0;
  text-transform: uppercase;
}

#page-home-page .carousel{
	height:400px;
	max-height: 400px;
}

#page-home-page .carousel .item img{
	width: 100%;
	height: 400px;
}
.about_kna p {
  font-size: 15px;
  line-height: 30px;
}

.about_kna h1 {
  margin-top: 20px !important;
}


@media (max-width: 480px) {

.main_wrapper.banner_sec h1 {
  font-size: 16px;
  padding: 10px 15px;
}

 }

.card-wrapper .bootcards-summary-item:hover {
  box-shadow: 0 5px 5px 1px rgba(0, 0, 0, 0.2);
}
