css:
body {
  background-attachment: fixed;
  background-size: cover;
  overflow-x: hidden;
  width: 100%;
 background-color: #ffffff;
}
header {
  border-bottom: 5px solid #2b955a;
}
.about-content .pull-left img {
  padding-right: 30px;
}
p {
  color: #606060;
}

.page-container { 
  max-width: 100%;
  width: 100%;
}
.bootcards-summary-item:hover .icon img {
  background: #333333;
}
.navbar-brand img {
  height: auto;
  max-width: 200px !important;
  width: 190px;
}
.navbar-brand {
  padding: 0;
}
.navbar-default .navbar-nav > li > a {
  font-size: 16px;
}
.nav > li {
  margin-top: 80px;
}

/*----------------*/

a:hover {
  text-decoration: none;
}
.carousel-control {
  display: none;
}
.footer_logo img {
  border: 10px solid #eee;
  border-radius: 10px;
  margin: 0 auto 30px;
  width: 250px;
}
.footer_social > ul {
  padding: 0 0 15px;
  text-align: center;
}
.footer_social li {
  display: inline-block;
  list-style-type: none;
  margin: 0 6px;
}
.footer_social a {
  border: 1px solid #fff;
  border-radius: 50%;
  color: #fff;
  display: block;
  font-size: 20px;
  height: 40px;
  line-height: 35px;
  text-align: center;
  width: 40px;
}
.footer_social a:hover {
  background: #ffffff;
}

.footer-bottom-line .text-muted.small {
  font-size: 0;
}
.small .footer_sec p {
  color: #ffffff;
  font-size: 15px;
}
.footer-powered a {
  font-size: 15px;
}
.footer-powered a {
  color: #ffffff !important;
  font-size: 15px;
}

.footer-bottom-line {
  margin-top: 0;
}
.web-footer {
  background-color: #333333;
  border-top: 1px solid #e6e6e6;
  padding: 30px 0;
}
.footer-bottom-line .col-sm-6 {
  text-align: center;
  width: 100%;
}
.web-sidebar .sidebar-item a {
  color: #222;
  font-size: 18px;
  font-weight: 600;
}
.page-content.with-sidebar {
  background: #ffffff none repeat scroll 0 0;
}
.form-control {
  height: 38px;
}
.btn, .btn:hover, .btn:active, .btn:focus {
  background: #229a4e none repeat scroll 0 0;
  border: 1px solid #229a4e;
  font-size: 15px;
  font-weight: 600;
  padding: 5px 30px;
  text-transform: uppercase;
  color: #ffffff;
}

p {
  font-size: 15px;
  line-height: 30px;
}
.page-head h1 {
  font-weight: 600;
}

.navbar-default .navbar-toggle:hover, .navbar-default .navbar-toggle:focus {
  background-color: #EC1A28;
}
.contact-content h4 {
  font-size: 20px;
  font-weight: 600;
}

.navbar-header .dropdown .btn {
  margin: 80px 20px 0 0;
  padding: 6px 12px;
}
.img_gallery {
  margin-bottom: 30px;
}
.page-content h1:first-child {
  color: #2b955a;
}
